// src/storyData.ts

type Answer = {
    text: string;
    nextPrompt?: Prompt; // This refers to the Prompt type below.
  };

  type Prompt = {
    question: string;
    answers: Answer[];
  };

  export const initialPrompt: Prompt = {
    question: "You notice the sky turning a dark greenish color...",
    answers: [
      {
        text: "Go outside to take a photo",
        nextPrompt: {
          question: "You step outside with your camera...",
          answers: [
            { text: "Continue trying to get the perfect shot..." },
            { text: "Realize the gravity of the situation and rush back inside..." },
            // ... more answers
          ],
        },
      },
      // ... other answers with their branching scenarios
    ],
  };
