import React, { useState } from 'react';

type Answer = {
  text: string;
  nextPrompt?: Prompt;
};

type Prompt = {
  question: string;
  answers: Answer[];
};

type Props = {
  data: Prompt;
};

export const PromptBox: React.FC<Props> = ({ data }) => {
  const [currentPrompt, setCurrentPrompt] = useState<Prompt | null>(data);

  if (!currentPrompt) return null;

  return (
    <div className="prompt-box">
      <p>{currentPrompt.question}</p>
      {currentPrompt.answers.map((answer, index: number) => (
        <button key={index} onClick={() => setCurrentPrompt(answer.nextPrompt || null)}>
          {answer.text}
        </button>
      ))}
    </div>
  );
}
