export type Answer = {
  text: string;
  nextPrompt?: Prompt;
  feedback?: string;
};

export type Prompt = {
  question: string;
  answers: Answer[];
};

type PromptBoxProps = {
  data: Prompt;
};

const PromptBox: React.FC<PromptBoxProps> = ({ data }) => {
  const [currentPrompt, setCurrentPrompt] = useState(data);
  const [feedback, setFeedback] = useState<string | null>(null);

  const handleAnswerClick = (answer: Answer) => {
    if (answer.feedback) {
      // Display feedback in the popup
      setFeedback(answer.feedback);
    } else if (answer.nextPrompt) {
      // Move to the next prompt
      setCurrentPrompt(answer.nextPrompt);
      setFeedback(null); // Clear feedback
    }
  };

  return (
    <div className="PromptBox">
      <h1>{currentPrompt.question}</h1>
      <ul>
        {currentPrompt.answers.map((answer, index) => (
          <li key={index}>
            <button onClick={() => handleAnswerClick(answer)}>{answer.text}</button>
          </li>
        ))}
      </ul>
      {feedback && (
        <div className="feedback-popup">
          <p>{feedback}</p>
          <button onClick={() => setFeedback(null)}>Continue</button>
        </div>
      )}
    </div>
  );
};

export default PromptBox;
