export type Answer = {
    text: string;
    nextPrompt?: Prompt;
};

export type Prompt = {
    question: string;
    answers: Answer[];
};

const earlyWarningPrompt: Prompt = {
    question: "You notice the sky turning a dark greenish color, and the wind starts picking up. What do you do next?",
    answers: [
        { text: "Go outside to take a photo." },
        { text: "Turn on the radio or TV for weather updates." },
        { text: "Ignore it; it might just be a regular storm." }
    ]
};

const tornadoWatchCorrect: Prompt = {
    question: "Correct! A 'tornado watch' means conditions are right for a tornado, but one hasn't been spotted yet. Stay alert and prepare accordingly.",
    answers: [
        { text: "Continue", nextPrompt: preparingHomePrompt }
    ]
};

const tornadoWatchIncorrect: Prompt = {
    question: "That's incorrect. A 'tornado watch' means conditions are right for a tornado, but one hasn't been spotted yet. Do you want to try again?",
    answers: [
        { text: "Yes, try again.", nextPrompt: tornadoWatchPrompt },
        { text: "No, end the simulation." }
    ]
};

const tornadoWatchPrompt: Prompt = {
    question: "You hear on the radio that a tornado watch has been issued for your area. This means:",
    answers: [
        { text: "A tornado has been spotted, and you should take cover immediately.", nextPrompt: tornadoWatchIncorrect },
        { text: "Conditions are right for a tornado, but one hasn't been spotted yet.", nextPrompt: tornadoWatchCorrect },
        { text: "There's a slight possibility of a tornado today.", nextPrompt: tornadoWatchIncorrect }
    ]
};

const preparingHomePrompt: Prompt = {
    question: "You have some time before the storm hits. What is the best way to prepare your home?",
    answers: [
        { text: "Open all windows to equalize pressure." },
        { text: "Secure outdoor furniture and move your car inside the garage." },
        { text: "Start cleaning your yard." }
    ]
};

const takingShelterPrompt: Prompt = {
    question: "The warning sirens are blaring. Where is the safest place to take shelter in your home?",
    answers: [
        { text: "In the southwest corner of your basement." },
        { text: "In a hallway without windows." },
        { text: "In your upstairs bedroom." }
    ]
};

const protectingYourselfPrompt: Prompt = {
    question: "You're in the basement, and you can hear the tornado approaching. What should you do to protect yourself?",
    answers: [
        { text: "Stand near a window to watch the tornado's path." },
        { text: "Get under something sturdy like a workbench or stairs and protect your head and neck." },
        { text: "Sit down in the center of the room." }
    ]
};

const postTornadoPrompt: Prompt = {
    question: "The tornado has passed, but you're unsure of the situation outside. What's the safest thing to do next?",
    answers: [
        { text: "Go outside immediately to check the damage." },
        { text: "Wait a few minutes and then leave the basement." },
        { text: "Listen to the radio or your emergency weather app for updates before deciding." }
    ]
};

const helpingNeighborsPrompt: Prompt = {
    question: "Your neighbors' house has been severely damaged, and they need assistance. What should you do first?",
    answers: [
        { text: "Head over immediately to offer help." },
        { text: "Ensure it's safe outside and take an emergency kit with you." },
        { text: "Wait for professional help to arrive." }
    ]
};

const sweptAwayByTornado: Prompt = {
    question: "The power of the tornado was too strong! You've been swept away by the storm. Luckily, this is just a simulation. Do you want to try again?",
    answers: [
        { text: "Yes, start over.", nextPrompt: earlyWarningPrompt },
        { text: "No, end the simulation." } // No nextPrompt; the experience would end here.
    ]
};

const ignoredWarning: Prompt = {
    question: "Unfortunately, it wasn't just a regular storm, but a tornado! You've been swept away by the storm. Thankfully, this is just a simulation. Do you want to try again?",
    answers: [
        { text: "Yes, start over.", nextPrompt: earlyWarningPrompt },
        { text: "No, end the simulation." } // No nextPrompt; the experience would end here.
    ]
};

// Linking up the sequence of prompts
earlyWarningPrompt.answers[0].nextPrompt = sweptAwayByTornado;
earlyWarningPrompt.answers[1].nextPrompt = tornadoWatchPrompt;
earlyWarningPrompt.answers[2].nextPrompt = ignoredWarning; 

tornadoWatchPrompt.answers[1].nextPrompt = preparingHomePrompt;  // Only the correct answer leads to the next stage.
preparingHomePrompt.answers.forEach(answer => answer.nextPrompt = takingShelterPrompt);
takingShelterPrompt.answers.forEach(answer => answer.nextPrompt = protectingYourselfPrompt);
protectingYourselfPrompt.answers.forEach(answer => answer.nextPrompt = postTornadoPrompt);
postTornadoPrompt.answers.forEach(answer => answer.nextPrompt = helpingNeighborsPrompt);

export const initialPrompts: Prompt[] = [earlyWarningPrompt];
