// src/storyData.ts

export type Answer = {
    text: string;
    nextPrompt?: Prompt;
};

export type Prompt = {
    question: string;
    answers: Answer[];
};

const tornadoWatchPrompt: Prompt = {
    question: "You hear on the radio that a tornado watch has been issued for your area. This means:",
    answers: [
        { text: "A tornado has been spotted, and you should take cover immediately.", nextPrompt: preparingHomePrompt },
        { text: "Conditions are right for a tornado, but one hasn't been spotted yet.", nextPrompt: preparingHomePrompt },
        { text: "There's a slight possibility of a tornado today.", nextPrompt: preparingHomePrompt }
    ],
};

const preparingHomePrompt: Prompt = {
    question: "You have some time before the storm hits. What is the best way to prepare your home?",
    answers: [
        { text: "Open all windows to equalize pressure.", nextPrompt: takingShelterPrompt },
        { text: "Secure outdoor furniture and move your car inside the garage.", nextPrompt: takingShelterPrompt },
        { text: "Start cleaning your yard.", nextPrompt: takingShelterPrompt }
    ],
};

const takingShelterPrompt: Prompt = {
    question: "The warning sirens are blaring. Where is the safest place to take shelter in your home?",
    answers: [
        { text: "In the southwest corner of your basement.", nextPrompt: protectingYourselfPrompt },
        { text: "In a hallway without windows.", nextPrompt: protectingYourselfPrompt },
        { text: "In your upstairs bedroom.", nextPrompt: protectingYourselfPrompt }
    ],
};

const protectingYourselfPrompt: Prompt = {
    question: "You're in the basement, and you can hear the tornado approaching. What should you do to protect yourself?",
    answers: [
        { text: "Stand near a window to watch the tornado's path.", nextPrompt: postTornadoPrompt },
        { text: "Get under something sturdy like a workbench or stairs and protect your head and neck.", nextPrompt: postTornadoPrompt },
        { text: "Sit down in the center of the room.", nextPrompt: postTornadoPrompt }
    ],
};

const postTornadoPrompt: Prompt = {
    question: "The tornado has passed, but you're unsure of the situation outside. What's the safest thing to do next?",
    answers: [
        { text: "Go outside immediately to check the damage.", nextPrompt: helpingNeighborsPrompt },
        { text: "Wait a few minutes and then leave the basement.", nextPrompt: helpingNeighborsPrompt },
        { text: "Listen to the radio or your emergency weather app for updates before deciding.", nextPrompt: helpingNeighborsPrompt }
    ],
};

const helpingNeighborsPrompt: Prompt = {
    question: "Your neighbors' house has been severely damaged, and they need assistance. What should you do first?",
    answers: [
        { text: "Head over immediately to offer help." }, // As this is the last prompt, no need for `nextPrompt` here
        { text: "Ensure it's safe outside and take an emergency kit with you." },
        { text: "Wait for professional help to arrive." }
    ],
};

export const initialPrompts: Prompt = {
    question: "You notice the sky turning a dark greenish color, and the wind starts picking up. What do you do next?",
    answers: [
        { text: "Go outside to take a photo.", nextPrompt: tornadoWatchPrompt },
        { text: "Turn on the radio or TV for weather updates.", nextPrompt: tornadoWatchPrompt },
        { text: "Ignore it; it might just be a regular storm.", nextPrompt: tornadoWatchPrompt }
    ],
};
